--@class KinematicsMin
function Kinematics()local a={}local b=30000000/3600;local c=b*b;local d=100;local function e(f)return 1/math.sqrt(1-f*f/c)end;function a.computeAccelerationTime(g,h,i)local j=b*math.asin(g/b)return(b*math.asin(i/b)-j)/h end;function a.computeDistanceAndTime(g,i,k,l,m,n)m=m or 0;n=n or 0;local o=e(g)local p=g<=i;local q=l*(p and 1 or-1)/k;local r=-n/k;local s=q+r;if p and s<=0 or not p and s>=0 then return-1,-1 end;local t,u=0,0;if q~=0 and m>0 then local j=math.asin(g/b)local v=math.pi*(q/2+r)local w=q*m;local x=b*math.pi;local f=function(y)local z=(v*y-w*math.sin(math.pi*y/2/m)+x*j)/x;local A=math.tan(z)return b*A/math.sqrt(A*A+1)end;local B=p and function(C)return C>=i end or function(C)return C<=i end;u=2*m;if B(f(u))then local D=0;while math.abs(u-D)>0.5 do local y=(u+D)/2;if B(f(y))then u=y else D=y end end end;local E=g;local F=u/d;for G=1,d do local H=f(G*F)t=t+(H+E)*F/2;E=H end;if u<2*m then return t,u end;g=E end;local j=b*math.asin(g/b)local I=(b*math.asin(i/b)-j)/s;local J=c*math.cos(j/b)/s;local K=J-c*math.cos((s*I+j)/b)/s;return K+t,I+u end;function a.computeTravelTime(g,h,K)if K==0 then return 0 end;if h>0 then local j=b*math.asin(g/b)local J=c*math.cos(j/b)/h;return(b*math.acos(h*(J-K)/c)-j)/h end;assert(g>0,'Acceleration and initial speed are both zero.')return K/g end;function a.lorentz(f)return e(f)end;return a end