--@class SimpleSlotDetectorMin
core=nil;antigrav=nil;warpDrive=nil;radarUnit=nil;flightModeDb=nil;manualSwitches={}forceFields={}laser=nil;screen=nil;settingsActive=false;emitter=nil;telemeter=nil;function getElements()for a,b in pairs(_G)do if type(b)=="table"and b["getElementClass"]then local c=b["getElementClass"]()if c=="CoreUnitDynamic"or c=="CoreUnitStatic"or c=="CoreUnitSpace"then core=b end;if c=="AtmoFuelContainer"or c=="SpaceFuelContainer"then b.show()end;if c=="WarpDriveUnit"then warpDrive=b;b.show()end;if c=="RadarPvPAtmospheric"or c=="RadarPvPSpace"then radarUnit=b;b.show()end;if c=="DataBankUnit"then flightModeDb=b end;if c=="AntiGravityGeneratorUnit"then antigrav=b end;if c=="ManualSwitchUnit"then table.insert(manualSwitches,b)end;if c=="ForceFieldUnit"then table.insert(forceFields,b)end;if c=="ScreenUnit"then screen=b end;if c=="LaserEmitterUnit"then laser=b end;if c=="EmitterUnit"then emitter=b end;if c=="TelemeterUnit"then telemeter=b end end end end;getElements()